<?php/** * Footer Banner page * * @author Your Inspiration Themes * @package YITH Footer Banner * @version 1.0.3 */do_action( 'yith_before_fbanner' );$image_url = get_option('yith_fbanner_image');?><!-- YITH-FOOTER-BANNER --><div class="fbanner-placeholder"></div><div class="fbanner">    <div id="fbannercont" class="group">        <?php if( !empty( $image_url ) ) : ?>        <div id="fbannerlogo" class="yith-border">            <?php if (get_option('yith_fbanner_link')):?><a href="<?php echo get_option('yith_fbanner_link');?>" title="<?php echo get_option('yith_fbanner_message') ?>"><?php endif;?>                <img src="<?php echo $image_url ?>" alt="<?php echo get_option('yith_fbanner_message') ?>"/>                <?php if (get_option('yith_fbanner_link')):?></a><?php endif;?>        </div>        <?php endif ?>        <ul id="fbannermess">            <li>                <h3>                    <?php if (get_option('yith_fbanner_link')):?><a href="<?php echo get_option('yith_fbanner_link');?>" title="<?php echo get_option('yith_fbanner_message') ?>"><?php endif;?>                        <?php echo get_option('yith_fbanner_message');?>                        <?php if (get_option('yith_fbanner_link')):?></a><?php endif;?>                </h3>            </li>            <li>                <p>                    <?php if ( get_option( 'yith_fbanner_link') ): ?><a href="<?php echo get_option('yith_fbanner_link');?>" title="<?php echo get_option('yith_fbanner_message') ?>"><?php endif;?>                        <?php echo get_option('yith_fbanner_sub_message');?>                        <?php if (get_option('yith_fbanner_link')):?></a><?php endif;?>                </p>            </li>        </ul>        <div id="fbannernews">            <?php if ( get_option('yith_fbanner_enable_newsletter_form') == true ): ?>                <form method="<?php echo get_option('yith_fbanner_newsletter_method') ?>" action="<?php echo get_option('yith_fbanner_newsletter_action') ?>" class="fbannernewsletter">                    <fieldset>                        <?php if(get_option('yith_fbanner_newsletter_name_label')){?>                            <p class="fbanner-top-text both-fields"><?php echo get_option('yith_fbanner_newsletter_top_text');?></p>                            <ul class="newsfields">                                <li class="newstop fbanner-fields">                                    <input type="text" name="<?php echo get_option('yith_fbanner_newsletter_name_name') ?>" id="<?php echo get_option('yith_fbanner_newsletter_name_name') ?>" class="name-field text-field" placeholder="<?php echo get_option('yith_fbanner_newsletter_name_label') ?>" />                                </li>                                <li class="newsbottom fbanner-fields">                                    <input type="text" name="<?php echo get_option('yith_fbanner_newsletter_email_name') ?>" id="<?php echo get_option('yith_fbanner_newsletter_email_name') ?>" class="email-field text-field" placeholder="<?php echo get_option('yith_fbanner_newsletter_email_label') ?>" />                                </li>                                <li class="newssubmit fbanner-fields">                                    <input type="submit" value="<?php echo get_option('yith_fbanner_newsletter_submit_label') ?>" class="submit-field newssubmit" />                                </li>                            </ul>                        <?php } else { ?>                            <div id="no-name-field">                                <p class="fbanner-top-text"><?php echo get_option('yith_fbanner_newsletter_top_text');?></p>                                <input type="text" name="<?php echo get_option('yith_fbanner_newsletter_email_name') ?>" id="<?php echo get_option('yith_fbanner_newsletter_email_name') ?>" class="email-field text-field" placeholder="<?php echo get_option('yith_fbanner_newsletter_email_label') ?>" />                                <input type="submit" value="<?php echo get_option('yith_fbanner_newsletter_submit_label') ?>" class="submit-field" />                            </div>                        <?php };?>                        <?php $hiddenfields = get_option('yith_fbanner_newsletter_hidden_fields');                        if ($hiddenfields) :                            $result = explode('&',$hiddenfields);                            foreach ($result as $hivalue ) :                                $formvalue = explode('=',$hivalue);?>                                <input type="hidden" id="<?php echo $formvalue[0] ?>" name="<?php echo $formvalue[0] ?>" value="<?php echo $formvalue[1] ?>" />                            <?php endforeach; endif;?>                    </fieldset>                </form>            <?php endif; ?>        </div>    </div><!-- fbannercont -->    <ul class="hiderzone">        <li>            <div id="showhideforever">                <a href="#" title="<?php _e( get_option('yith_fbanner_hide_forever_message') ,'yit');?>">                    <?php echo _e( get_option('yith_fbanner_hide_forever_message') ,'yit');?>                </a>            </div>        </li>        <li>            <div id="showhidefbanner">                <a href="#" title="<?php _e( get_option('yith_fbanner_hide_message') ,'yit');?>">                    <?php _e( get_option('yith_fbanner_hide_message') ,'yit');?>                </a>            </div>        </li>    </ul></div><!-- fbanner --><!-- YITH-FOOTER-BANNER -->